import React, { useState } from "react";
import AirspaceMap from "./components/Map/AirspaceMap";
import CommandPanel from "./components/Controller/CommandPanel";
import PilotATCPanel from "./components/Controller/PilotAtcPanel";

function App() {
  const [role, setRole] = useState("controller"); // controller or pilot
  const [selectedCallsign, setSelectedCallsign] = useState(null);

  const [planes, setPlanes] = useState([
    { icao: "LZIB", flight_level: 350, lat: 44.57, lng: 27.48, heading: 90, speed: 450, callsign: "BUL123", trail: [[44.57, 27.48]] },
    { icao: "A320", flight_level: 320, lat: 44.87, lng: 26.48, heading: 270, speed: 430, callsign: "BUL456", trail: [[44.87, 26.48]] },
    // alte avioane...
  ]);

  return (
    <div style={{ height: "100vh", width: "100vw", position: "relative" }}>
      {/* Role toggle */}
      <button
        style={{ position: "fixed", top: 10, left: 10, zIndex: 1000 }}
        onClick={() => setRole(role === "pilot" ? "controller" : "pilot")}
      >
        Switch to {role === "pilot" ? "Controller" : "Pilot"} View
      </button>

      {/* Panel */}
      {role === "controller" && (
        <CommandPanel
          plane={planes.find((p) => p.callsign === selectedCallsign)}
          onSendCommand={(callsign, type, value) =>
            alert(`Sent command to ${callsign}: ${type} -> ${value}`)
          }
        />
      )}
      {role === "pilot" && (
        <PilotATCPanel
          plane={planes.find((p) => p.callsign === selectedCallsign)}
          onAccept={(cmd) => alert(`Accepted command ${cmd.type}`)}
          onDeny={(cmd) => alert(`Denied command ${cmd.type}`)}
        />
      )}

      {/* Harta */}
      <AirspaceMap
        planes={planes}
        selectedCallsign={selectedCallsign}
        setSelectedCallsign={setSelectedCallsign}
        showClouds={true}
        showWind={true}
        showPressure={true}
      />
    </div>
  );
}

export default App;
