import React from "react";
// Import components from react-leaflet for map rendering
import { MapContainer, TileLayer } from "react-leaflet";
// Import Leaflet's CSS for proper map styling
import "leaflet/dist/leaflet.css";

function App() {
  return (
    // Set the map to fill the entire viewport
    <div style={{ height: "100vh", width: "100vw" }}>
      {/* 
        MapContainer is the main component for displaying the map.
        - center: [latitude, longitude] sets the initial center of the map (here, over Romania).
        - zoom: sets the initial zoom level.
        - style: ensures the map fills the parent div.
      */}
      <MapContainer center={[45, 25]} zoom={8} style={{ height: "100%", width: "100%" }}>
        {/* 
          TileLayer loads the map tiles from OpenStreetMap.
          - url: template for tile images.
          - attribution: required by OpenStreetMap for legal use.
        */}
        <TileLayer
          url="https://{s}.tile.openstreetmap.org/{z}/{x}/{y}.png"
          attribution="&copy; OpenStreetMap contributors"
        />
      </MapContainer>
    </div>
  );
}

export default App;
