// function for controller view
function CommandPanel({ plane, onSendCommand }) {
  if (!plane) return <div>Select an aircraft</div>;

  return (
    <div
      style={{
        position: "absolute",
        top: 150,
        left: 10,
        zIndex: 1000,
        background: "#222",
        color: "#fff",
        padding: 10,
      }}
    >
      <h4>Controller Panel</h4>
      <div>
        <strong>Selected:</strong> {plane.callsign}
      </div>
      <button
        onClick={() =>
          onSendCommand(plane.callsign, "heading", plane.heading + 10)
        }
      >
        Change Heading +10Â°
      </button>
      <button
        onClick={() =>
          onSendCommand(plane.callsign, "altitude", plane.flight_level + 1000)
        }
      >
        Change Altitude +1000
      </button>
      <button
        onClick={() => onSendCommand(plane.callsign, "speed", plane.speed + 50)}
      >
        Increase Speed +50
      </button>

      <div style={{ marginTop: 10 }}>
        <h5>Command History:</h5>
        <ul>
          {plane.commandsHistory?.map((cmd, idx) => (
            <li key={idx}>
              {cmd.type}: {cmd.value} ({cmd.status})
            </li>
          ))}
        </ul>
      </div>
    </div>
  );
}

export default CommandPanel;
