import React from "react";
import { MapContainer as LeafletMap, TileLayer } from "react-leaflet";
import "leaflet/dist/leaflet.css";

const MapContainer = ({ children, center = [45.9432, 24.9668], zoom = 7 }) => {
  return (
    <LeafletMap center={center} zoom={zoom} style={{ height: "100%", width: "100%" }} scrollWheelZoom={true}>
      {/* Dacă vrei Dark Matter folosește URL-ul Carto din exemplu inițial */}
      <TileLayer
        url="https://{s}.basemaps.cartocdn.com/dark_all/{z}/{x}/{y}{r}.png"
        attribution='&copy; <a href="https://www.openstreetmap.org/copyright">OpenStreetMap</a> contributors &copy; CARTO'
        subdomains={["a", "b", "c", "d"]}
        maxZoom={20}
      />
      {children}
    </LeafletMap>
  );
};

export default MapContainer;
